import React, { useEffect, useState } from "react";
import API from "../api";
import { useNavigate } from "react-router-dom";

export default function EditProfilePage() {
  const [user, setUser] = useState(null);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    API.get("/auth/profile")
      .then((res) => setUser(res.data))
      .catch(() => navigate("/login"));
  }, [navigate]);

  if (!user) {
    return <p className="text-center mt-10">Loading...</p>;
  }

  const handleSave = async (e) => {
    e.preventDefault();

    try {
      await API.put("/auth/update-profile", user);
      setMessage("Profile updated successfully!");

      setTimeout(() => navigate("/profile"), 1500);
    } catch (err) {
      setMessage("Failed to update profile.");
    }
  };

  const updateField = (field, value) => {
    setUser({ ...user, [field]: value });
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white rounded-xl shadow-xl p-8 w-full max-w-xl">
        
        <h2 className="text-2xl font-bold text-center text-indigo-600 mb-6">
          Edit Profile
        </h2>

        <form onSubmit={handleSave} className="space-y-4">

          {/* Phone */}
          <div>
            <label className="block text-gray-700 font-medium mb-1">Phone:</label>
            <input
              className="w-full p-3 border rounded-lg"
              value={user.phone || ""}
              placeholder="Enter phone number"
              onChange={(e) => updateField("phone", e.target.value)}
            />
          </div>

          {/* Age */}
          <div>
            <label className="block text-gray-700 font-medium mb-1">Age:</label>
            <input
              className="w-full p-3 border rounded-lg"
              type="number"
              value={user.age || ""}
              placeholder="Enter age"
              onChange={(e) => updateField("age", e.target.value)}
            />
          </div>

          {/* Height */}
          <div>
            <label className="block text-gray-700 font-medium mb-1">Height (cm):</label>
            <input
              className="w-full p-3 border rounded-lg"
              type="number"
              value={user.height || ""}
              placeholder="Enter height in cm"
              onChange={(e) => updateField("height", e.target.value)}
            />
          </div>

          {/* Weight */}
          <div>
            <label className="block text-gray-700 font-medium mb-1">Weight (kg):</label>
            <input
              className="w-full p-3 border rounded-lg"
              type="number"
              value={user.weight || ""}
              placeholder="Enter weight in kg"
              onChange={(e) => updateField("weight", e.target.value)}
            />
          </div>

          {/* Skin Type */}
          <div>
            <label className="block text-gray-700 font-medium mb-1">Skin Type:</label>
            <input
              className="w-full p-3 border rounded-lg"
              value={user.skinType || ""}
              placeholder="Oily, Dry, Normal, etc."
              onChange={(e) => updateField("skinType", e.target.value)}
            />
          </div>

          {/* Hair Type */}
          <div>
            <label className="block text-gray-700 font-medium mb-1">Hair Type:</label>
            <input
              className="w-full p-3 border rounded-lg"
              value={user.hairType || ""}
              placeholder="Curly, Straight, Wavy, etc."
              onChange={(e) => updateField("hairType", e.target.value)}
            />
          </div>

          {/* Body Goal */}
          <div>
            <label className="block text-gray-700 font-medium mb-1">Body Goal:</label>
            <input
              className="w-full p-3 border rounded-lg"
              value={user.bodyGoal || ""}
              placeholder="Gain muscle, Lose weight, etc."
              onChange={(e) => updateField("bodyGoal", e.target.value)}
            />
          </div>

          {/* Allergies */}
          <div>
            <label className="block text-gray-700 font-medium mb-1">Allergies:</label>
            <textarea
              className="w-full p-3 border rounded-lg"
              value={user.allergies || ""}
              placeholder="List any allergies"
              onChange={(e) => updateField("allergies", e.target.value)}
            />
          </div>

          {/* Daily Routine */}
          <div>
            <label className="block text-gray-700 font-medium mb-1">Daily Routine:</label>
            <textarea
              className="w-full p-3 border rounded-lg"
              value={user.dailyRoutine || ""}
              placeholder="Describe your routine"
              onChange={(e) => updateField("dailyRoutine", e.target.value)}
            />
          </div>

          <button
            className="w-full bg-indigo-600 text-white p-3 rounded-lg hover:bg-indigo-700"
          >
            Save Changes
          </button>
        </form>

        {message && (
          <p className="text-center mt-4 text-green-600 font-semibold">{message}</p>
        )}
      </div>
    </div>
  );
}
