import React, { useState, useEffect } from "react";
import axios from "axios";

export default function ProfilePage() {
  const [profile, setProfile] = useState({
    age: "",
    weight_kg: "",
    height_cm: "",
    allergies: "",
    daily_routine: "",
    skin_type: "",
    hair_type: "",
    body_goal: "",
  });
  const [message, setMessage] = useState("");
  const token = localStorage.getItem("promptpal_token");

  useEffect(() => {
    axios
      .get("http://localhost:8080/api/profile", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setProfile(res.data))
      .catch(() => {});
  }, []);

  const handleSave = async (e) => {
    e.preventDefault();
    try {
      await axios.put("http://localhost:8080/api/profile", profile, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessage("✅ Profile updated successfully!");
    } catch {
      setMessage("❌ Error updating profile.");
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-teal-50 to-blue-100">
      <div className="max-w-lg w-full bg-white shadow-xl rounded-2xl p-8">
        <h2 className="text-3xl font-bold text-center text-teal-700 mb-6">Your Wellness Profile</h2>
        <form onSubmit={handleSave} className="grid grid-cols-1 gap-4">
          {["age", "weight_kg", "height_cm"].map((field) => (
            <input
              key={field}
              className="border border-gray-300 rounded-lg p-3 focus:ring-2 focus:ring-teal-500"
              placeholder={field.replace("_", " ").toUpperCase()}
              value={profile[field]}
              onChange={(e) => setProfile({ ...profile, [field]: e.target.value })}
            />
          ))}
          <textarea
            className="border border-gray-300 rounded-lg p-3 h-24 focus:ring-2 focus:ring-teal-500"
            placeholder="Describe your daily routine (min 100 chars)"
            value={profile.daily_routine}
            onChange={(e) => setProfile({ ...profile, daily_routine: e.target.value })}
          />
          <input
            className="border border-gray-300 rounded-lg p-3 focus:ring-2 focus:ring-teal-500"
            placeholder="Skin Type (e.g., oily, dry, balanced)"
            value={profile.skin_type}
            onChange={(e) => setProfile({ ...profile, skin_type: e.target.value })}
          />
          <input
            className="border border-gray-300 rounded-lg p-3 focus:ring-2 focus:ring-teal-500"
            placeholder="Hair Type (e.g., dry, oily, etc.)"
            value={profile.hair_type}
            onChange={(e) => setProfile({ ...profile, hair_type: e.target.value })}
          />
          <input
            className="border border-gray-300 rounded-lg p-3 focus:ring-2 focus:ring-teal-500"
            placeholder="Body Goal (e.g., gain, maintain, cut)"
            value={profile.body_goal}
            onChange={(e) => setProfile({ ...profile, body_goal: e.target.value })}
          />
          <button
            type="submit"
            className="bg-teal-600 hover:bg-teal-700 text-white font-semibold py-2 px-4 rounded-lg transition-all"
          >
            Save Profile
          </button>
        </form>
        {message && <p className="text-center mt-4 text-gray-600">{message}</p>}
      </div>
    </div>
  );
}
