import React, { useState, useEffect } from "react";
import axios from "axios";

export default function ProfilePage() {
  const [profile, setProfile] = useState({
    age: "",
    weight_kg: "",
    height_cm: "",
    allergies: "",
    daily_routine: "",
    skin_type: "",
    hair_type: "",
    body_goal: "",
  });
  const [message, setMessage] = useState("");

  const token = localStorage.getItem("promptpal_token");

  useEffect(() => {
    axios
      .get("http://localhost:8080/api/profile", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setProfile(res.data))
      .catch(() => {});
  }, []);

  const handleSave = async (e) => {
    e.preventDefault();
    try {
      await axios.put("http://localhost:8080/api/profile", profile, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessage("Profile saved successfully!");
    } catch {
      setMessage("Error saving profile.");
    }
  };

  return (
    <div className="card">
      <h2>Profile</h2>
      <form onSubmit={handleSave}>
        <input
          type="number"
          placeholder="Age"
          value={profile.age}
          onChange={(e) => setProfile({ ...profile, age: e.target.value })}
        />
        <input
          type="number"
          placeholder="Weight (kg)"
          value={profile.weight_kg}
          onChange={(e) => setProfile({ ...profile, weight_kg: e.target.value })}
        />
        <input
          type="number"
          placeholder="Height (cm)"
          value={profile.height_cm}
          onChange={(e) => setProfile({ ...profile, height_cm: e.target.value })}
        />
        <textarea
          placeholder="Daily routine"
          value={profile.daily_routine}
          onChange={(e) =>
            setProfile({ ...profile, daily_routine: e.target.value })
          }
        ></textarea>
        <button className="primary" type="submit">
          Save
        </button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}
