import React, { useState } from "react";
import axios from "axios";

export default function ForgotPasswordPage() {
  const [method, setMethod] = useState(""); // "email" or "phone"
  const [emailOrPhone, setEmailOrPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [step, setStep] = useState(1);
  const [message, setMessage] = useState("");

  // STEP 1 → SEND OTP
  const handleSendOtp = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post(
        "http://localhost:8080/api/auth/forgot-password",
        {
          method,
          emailOrPhone,
        }
      );

      setMessage(res.data);
      setStep(2);
    } catch (err) {
      const msg =
        err.response?.data?.error ||
        err.response?.data?.message ||
        "Failed to send OTP.";
      setMessage(msg);
    }
  };

  // STEP 2 → RESET PASSWORD
  const handleResetPassword = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post(
        `http://localhost:8080/api/auth/reset-password?emailOrPhone=${emailOrPhone}&otp=${otp}&newPassword=${newPassword}`
      );

      setMessage(res.data);
      setStep(3);
    } catch (err) {
      const msg =
        err.response?.data?.error ||
        err.response?.data?.message ||
        "Failed to reset password.";
      setMessage(msg);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 px-4">
      <div className="w-full max-w-md p-6 bg-white rounded-xl shadow-lg">
        <h2 className="text-2xl font-bold text-center text-indigo-700 mb-4">
          Forgot Password
        </h2>

        {message && (
          <p className="text-center text-sm text-red-600 mb-2">{message}</p>
        )}

        {/* ---------------- STEP 1 : SELECT METHOD + ENTER EMAIL/PHONE ---------------- */}
        {step === 1 && (
          <form onSubmit={handleSendOtp} className="space-y-4">
            <label className="font-medium">Choose OTP Method:</label>

            <select
              className="w-full border p-2 rounded"
              value={method}
              onChange={(e) => setMethod(e.target.value)}
              required
            >
              <option value="">-- Select --</option>
              <option value="email">Email</option>
              <option value="phone">Phone Number</option>
            </select>

            <input
              type="text"
              placeholder={
                method === "phone" ? "Enter Phone Number" : "Enter Email"
              }
              value={emailOrPhone}
              onChange={(e) => setEmailOrPhone(e.target.value)}
              className="w-full border p-2 rounded"
              required
            />

            <button
              type="submit"
              className="w-full bg-indigo-600 text-white py-2 rounded-lg"
            >
              Send OTP
            </button>
          </form>
        )}

        {/* ---------------- STEP 2 : ENTER OTP + NEW PASSWORD ---------------- */}
        {step === 2 && (
          <form onSubmit={handleResetPassword} className="space-y-4">
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              className="w-full border p-2 rounded"
              required
            />

            <input
              type="password"
              placeholder="Enter New Password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              className="w-full border p-2 rounded"
              required
            />

            <button
              type="submit"
              className="w-full bg-green-600 text-white py-2 rounded-lg"
            >
              Reset Password
            </button>
          </form>
        )}

        {/* ---------------- STEP 3 : SUCCESS ---------------- */}
        {step === 3 && (
          <div className="text-center">
            <p className="text-green-600 font-medium mb-3">
              Password reset successfully!
            </p>
            <a
              href="/login"
              className="text-indigo-600 font-semibold hover:underline"
            >
              Go to Login
            </a>
          </div>
        )}
      </div>
    </div>
  );
}
