import React, { useEffect, useState } from "react";
import API from "../api";
import { useNavigate } from "react-router-dom";

export default function EditProfilePage() {
  const [user, setUser] = useState(null);
  const [message, setMessage] = useState("");
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    API.get("/auth/profile")
      .then((res) => setUser(res.data))
      .catch(() => navigate("/login"));
  }, [navigate]);
  

  if (!user) return <p className="text-center mt-10">Loading...</p>;

  // -----------------------------------------
  // VALIDATION LOGIC
  // -----------------------------------------
  const validate = () => {
    let temp = {};

    if (!user.phone?.match(/^[0-9]{10}$/))
      temp.phone = "Phone must be 10 numeric digits.";

    if (user.age < 5 || user.age > 100)
      temp.age = "Age must be between 5 and 100.";

    if (user.height < 50 || user.height > 250)
      temp.height = "Height must be between 50–250 cm.";

    if (user.weight < 20 || user.weight > 300)
      temp.weight = "Weight must be between 20–300 kg.";

    if (!user.skinType)
      temp.skinType = "Please select a skin type.";

    if (!user.hairType)
      temp.hairType = "Please select a hair type.";

    if (!user.bodyGoal)
      temp.bodyGoal = "Please select a body goal.";

    setErrors(temp);
    return Object.keys(temp).length === 0;
  };

  // -----------------------------------------
  // Save Profile
  // -----------------------------------------
  const handleSave = async (e) => {
    e.preventDefault();

    if (!validate()) return;

    try {
      await API.put("/auth/update-profile", user);
      setMessage("Profile updated successfully!");

      setTimeout(() => navigate("/profile"), 1500);
    } catch (err) {
      setMessage("Failed to update profile.");
    }
  };

  const updateField = (field, value) => {
    setUser({ ...user, [field]: value });
  };

  // -----------------------------------------
  // Dropdown Values
  // -----------------------------------------
  const skinTypes = ["Oily", "Dry", "Normal", "Combination", "Sensitive"];
  const hairTypes = ["Straight", "Wavy", "Curly", "Coily", "Frizzy"];
  const bodyGoals = ["Lose Weight", "Gain Muscle", "Maintain", "Bulk", "Cut"];

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white rounded-xl shadow-xl p-8 w-full max-w-xl">

        <h2 className="text-2xl font-bold text-center text-indigo-600 mb-6">
          Edit Profile
        </h2>

        <form onSubmit={handleSave} className="space-y-4">

          {/* PHONE */}
          <div>
            <label className="font-medium text-gray-700">Phone:</label>
            <input
              className="w-full p-3 border rounded-lg"
              value={user.phone || ""}
              placeholder="Enter phone number"
              onChange={(e) => updateField("phone", e.target.value)}
            />
            {errors.phone && <p className="text-red-600 text-sm">{errors.phone}</p>}
          </div>

          {/* AGE */}
          <div>
            <label className="font-medium text-gray-700">Age:</label>
            <input
              type="number"
              className="w-full p-3 border rounded-lg"
              value={user.age || ""}
              onChange={(e) => updateField("age", e.target.value)}
            />
            {errors.age && <p className="text-red-600 text-sm">{errors.age}</p>}
          </div>

          {/* HEIGHT */}
          <div>
            <label className="font-medium text-gray-700">Height (cm):</label>
            <input
              type="number"
              className="w-full p-3 border rounded-lg"
              value={user.height || ""}
              onChange={(e) => updateField("height", e.target.value)}
            />
            {errors.height && <p className="text-red-600 text-sm">{errors.height}</p>}
          </div>

          {/* WEIGHT */}
          <div>
            <label className="font-medium text-gray-700">Weight (kg):</label>
            <input
              type="number"
              className="w-full p-3 border rounded-lg"
              value={user.weight || ""}
              onChange={(e) => updateField("weight", e.target.value)}
            />
            {errors.weight && <p className="text-red-600 text-sm">{errors.weight}</p>}
          </div>

          {/* SKIN TYPE DROPDOWN */}
          <div>
            <label className="font-medium text-gray-700">Skin Type:</label>
            <select
              className="w-full p-3 border rounded-lg"
              value={user.skinType || ""}
              onChange={(e) => updateField("skinType", e.target.value)}
            >
              <option value="">-- Select Skin Type --</option>
              {skinTypes.map((s) => (
                <option key={s} value={s}>{s}</option>
              ))}
            </select>
            {errors.skinType && <p className="text-red-600 text-sm">{errors.skinType}</p>}
          </div>

          {/* HAIR TYPE DROPDOWN */}
          <div>
            <label className="font-medium text-gray-700">Hair Type:</label>
            <select
              className="w-full p-3 border rounded-lg"
              value={user.hairType || ""}
              onChange={(e) => updateField("hairType", e.target.value)}
            >
              <option value="">-- Select Hair Type --</option>
              {hairTypes.map((h) => (
                <option key={h} value={h}>{h}</option>
              ))}
            </select>
            {errors.hairType && <p className="text-red-600 text-sm">{errors.hairType}</p>}
          </div>

          {/* BODY GOAL DROPDOWN */}
          <div>
            <label className="font-medium text-gray-700">Body Goal:</label>
            <select
              className="w-full p-3 border rounded-lg"
              value={user.bodyGoal || ""}
              onChange={(e) => updateField("bodyGoal", e.target.value)}
            >
              <option value="">-- Select Body Goal --</option>
              {bodyGoals.map((b) => (
                <option key={b} value={b}>{b}</option>
              ))}
            </select>
            {errors.bodyGoal && <p className="text-red-600 text-sm">{errors.bodyGoal}</p>}
          </div>

          {/* ALLERGIES */}
          <div>
            <label className="font-medium text-gray-700">Allergies:</label>
            <textarea
              className="w-full p-3 border rounded-lg"
              value={user.allergies || ""}
              onChange={(e) => updateField("allergies", e.target.value)}
            />
          </div>

          {/* DAILY ROUTINE */}
          <div>
            <label className="font-medium text-gray-700">Daily Routine:</label>
            <textarea
              className="w-full p-3 border rounded-lg"
              value={user.dailyRoutine || ""}
              onChange={(e) => updateField("dailyRoutine", e.target.value)}
            />
          </div>

          <button
            className="w-full bg-indigo-600 text-white p-3 rounded-lg hover:bg-indigo-700"
          >
            Save Changes
          </button>
        </form>

        {message && (
          <p className="text-center mt-4 text-green-600 font-semibold">{message}</p>
        )}
      </div>
    </div>
  );
}
