import React, { useState } from "react";
import axios from "axios";

export default function ForgotPasswordPage() {
  const [method, setMethod] = useState(""); // "email" or "phone"
  const [emailOrPhone, setEmailOrPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [step, setStep] = useState(1);
  const [message, setMessage] = useState("");

  const handleSendOtp = async () => {
    try {
      const res = await axios.post("http://localhost:8080/api/auth/forgot-password", {
        method,
        emailOrPhone,
      });
      setMessage(res.data);
      setStep(2);
    } catch (err) {
      setMessage(err.response?.data || "Failed to send OTP.");
    }
  };

  const handleResetPassword = async () => {
    try {
      const res = await axios.post(
        `http://localhost:8080/api/auth/reset-password?emailOrPhone=${emailOrPhone}&otp=${otp}&newPassword=${newPassword}`
      );
      setMessage(res.data);
      setStep(3);
    } catch (err) {
      setMessage(err.response?.data || "Failed to reset password.");
    }
  };

  return (
    <div className="relative min-h-screen flex items-center justify-center overflow-hidden">
      {/* Background video */}
      <video
        autoPlay
        loop
        muted
        playsInline
        className="absolute top-0 left-0 w-full h-full object-cover"
      >
        <source src="/videos/background.mp4" type="video/mp4" />
      </video>

      <div className="absolute inset-0 bg-black/40"></div>

      <div className="relative z-10 w-full max-w-md bg-white bg-opacity-95 rounded-2xl shadow-2xl p-8 border border-gray-100">
        <h2 className="text-3xl font-bold text-center text-indigo-700 mb-6">
          Forgot Password
        </h2>

        {/* STEP 1 - Choose method */}
        {step === 1 && (
          <>
            <div className="flex justify-between mb-4">
              <button
                className={`w-1/2 mr-2 py-2 rounded-lg font-semibold ${
                  method === "email"
                    ? "bg-indigo-600 text-white"
                    : "bg-gray-200 text-gray-800"
                }`}
                onClick={() => setMethod("email")}
              >
                By Email
              </button>
              <button
                className={`w-1/2 py-2 rounded-lg font-semibold ${
                  method === "phone"
                    ? "bg-indigo-600 text-white"
                    : "bg-gray-200 text-gray-800"
                }`}
                onClick={() => setMethod("phone")}
              >
                By Phone
              </button>
            </div>

            {method && (
              <>
                <input
                  type={method === "email" ? "email" : "text"}
                  placeholder={
                    method === "email"
                      ? "Enter your registered email"
                      : "Enter your registered phone number"
                  }
                  value={emailOrPhone}
                  onChange={(e) => setEmailOrPhone(e.target.value)}
                  className="w-full border p-3 rounded-lg mb-4"
                />
                <button
                  onClick={handleSendOtp}
                  className="w-full bg-indigo-600 text-white py-2 rounded-lg font-semibold hover:bg-indigo-700"
                >
                  Send OTP
                </button>
              </>
            )}
          </>
        )}

        {/* STEP 2 - Verify OTP */}
        {step === 2 && (
          <>
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              className="w-full border p-3 rounded-lg mb-3"
            />
            <input
              type="password"
              placeholder="Enter new password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              className="w-full border p-3 rounded-lg mb-3"
            />
            <button
              onClick={handleResetPassword}
              className="w-full bg-green-600 text-white py-2 rounded-lg font-semibold hover:bg-green-700"
            >
              Reset Password
            </button>
          </>
        )}

        {/* STEP 3 - Success */}
        {step === 3 && (
          <p className="text-center text-green-700 font-semibold">
            âœ… Password reset successful! You can now log in.
          </p>
        )}

        {message && (
          <p className="text-center mt-4 text-gray-700">{message}</p>
        )}
      </div>
    </div>
  );
}
