import React, { useState } from "react";
import axios from "axios";

export default function ForgotPasswordPage() {
  const [emailOrPhone, setEmailOrPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [step, setStep] = useState(1);
  const [message, setMessage] = useState("");

  const handleSendOtp = async () => {
    try {
      const res = await axios.post(
        `http://localhost:8080/api/auth/forgot-password?emailOrPhone=${emailOrPhone}`
      );
      setMessage(res.data);
      setStep(2);
    } catch (err) {
      setMessage(err.response?.data || "Failed to send OTP.");
    }
  };

  const handleResetPassword = async () => {
    try {
      const res = await axios.post(
        `http://localhost:8080/api/auth/reset-password?emailOrPhone=${emailOrPhone}&otp=${otp}&newPassword=${newPassword}`
      );
      setMessage(res.data);
      setStep(3);
    } catch (err) {
      setMessage(err.response?.data || "Failed to reset password.");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-blue-100">
      <div className="bg-white shadow-xl p-8 rounded-lg w-96">
        <h2 className="text-2xl font-bold mb-4 text-center text-blue-600">
          Forgot Password
        </h2>

        {step === 1 && (
          <>
            <input
              type="text"
              placeholder="Enter Email or Phone"
              value={emailOrPhone}
              onChange={(e) => setEmailOrPhone(e.target.value)}
              className="w-full border p-2 rounded mb-4"
            />
            <button
              onClick={handleSendOtp}
              className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
            >
              Send OTP
            </button>
          </>
        )}

        {step === 2 && (
          <>
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              className="w-full border p-2 rounded mb-3"
            />
            <input
              type="password"
              placeholder="New Password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              className="w-full border p-2 rounded mb-3"
            />
            <button
              onClick={handleResetPassword}
              className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700"
            >
              Reset Password
            </button>
          </>
        )}

        {step === 3 && (
          <div className="text-center text-green-700">
            Password reset successful! You can now log in.
          </div>
        )}

        {message && <p className="mt-4 text-center text-gray-700">{message}</p>}
      </div>
    </div>
  );
}
