import React from "react";
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from "react-router-dom";
import SignupPage from "./pages/SignupPage";
import LoginPage from "./pages/LoginPage";
import ProfilePage from "./pages/ProfilePage";

import ForgotPasswordPage from "./pages/ForgotPasswordPage";


<Route path="/forgot-password" element={<ForgotPasswordPage />} />


// Helper auth functions
function getToken() {
  return localStorage.getItem("promptpal_token");
}

function logout() {
  localStorage.removeItem("promptpal_token");
  window.location.href = "/login";
}

export default function App() {
  const isAuthenticated = !!getToken();

  return (
    <Router>
      <div className="min-h-screen bg-gray-50 text-gray-800">
        {/* Header */}
        <header className="bg-white shadow-sm">
          <div className="max-w-6xl mx-auto px-4 py-4 flex justify-between items-center">
            <Link to="/" className="font-bold text-xl text-blue-700">
              PROMPTPAL
            </Link>

            <nav className="space-x-4">
              {!isAuthenticated ? (
                <>
                  <Link
                    to="/signup"
                    className="hover:underline hover:text-blue-600"
                  >
                    Signup
                  </Link>
                  <Link
                    to="/login"
                    className="hover:underline hover:text-blue-600"
                  >
                    Login
                  </Link>
                </>
              ) : (
                <>
                  <Link
                    to="/profile"
                    className="hover:underline hover:text-blue-600"
                  >
                    Profile
                  </Link>
                  <Link
                    to="/dashboard"
                    className="hover:underline hover:text-blue-600"
                  >
                    Dashboard
                  </Link>
                  <button
                    onClick={logout}
                    className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                  >
                    Logout
                  </button>
                </>
              )}
            </nav>
          </div>
        </header>

        {/* Main content */}
        <main className="max-w-5xl mx-auto p-4">
          <Routes>
            {/* Redirect to correct page */}
            <Route
              path="/"
              element={
                <Navigate to={isAuthenticated ? "/dashboard" : "/signup"} />
              }
            />
            <Route path="/signup" element={<SignupPage />} />
            <Route path="/login" element={<LoginPage />} />
            <Route path="/forgot-password" element={<ForgotPasswordPage />} />
            <Route path="/profile" element={<ProfilePage />} />


            <Route
              path="/profile"
              element={
                isAuthenticated ? <ProfilePage /> : <Navigate to="/login" />
              }
            />

          </Routes>
        </main>

        {/* Footer */}
        <footer className="text-center py-4 text-gray-500 text-sm">
          © {new Date().getFullYear()} PROMPTPAL — Personalized AI Wellness
        </footer>
      </div>
    </Router>
  );
}
